mapper.insert:
insert into Follow
(
FOL_NUMBER
,FOLLOWER_NUMBER
,FOLLOW_NUMBER
)
values
(
SEQ_Follow_fol_number.nextval
,#{follower_number}
,#{follow_number}
)

mapper.select:
select count(*)
from Follow
where
FOLLOWER_NUMBER=#{follower_number}
and
FOLLOW_NUMBER =#{follow_number}

mapper.delete:
delete from Follow
where
FOLLOWER_NUMBER=#{follower_number}
and
FOLLOW_NUMBER =#{follow_number}
