mapper.insert:
insert into Post
(
post_number
,mus_number
,cust_number
,mus_title
,post_content
,post_date
,post_nickname
,post_reply
,post_like
,post_play
,post_original
,post_saved
)
values
(
SEQ_Post_post_number.nextval
,#{mus_number}
,#{cust_number}
,#{mus_title}
,#{post_content}
,sysdate
,#{post_nickname}
,0
,0
,0
,#{post_original}
,#{post_saved}
)

mapper.select:
select post_number
from Post
where mus_number = #{mus_number}

and post_content = #{post_content}

 (select NVL(max(likt_type),0)  from Like_click
where post_number =#{post_number}
 and  cust_number =#{cust_number}
 and target_number=#{target_number})

mapper.insert:
insert into Like_click
(
like_number
,post_number
,cust_number
,target_number
,likt_type
)
values
(
SEQ_like_click_like_number.nextval
,#{post_number}
,#{cust_number}
,#{target_number}
,'1'
)

mapper.delete:
	delete from Like_click
where post_number =#{post_number}
 and  cust_number =#{cust_number}
 and target_number=#{target_number}
